INSERT INTO `config_info` VALUES (1, 'application-dev.yml', 'DEFAULT_GROUP', 'spring:\n  autoconfigure:\n    exclude: com.alibaba.druid.spring.boot.autoconfigure.DruidDataSourceAutoConfigure\n\n# feign 配置\nfeign:\n  sentinel:\n    enabled: true\n  okhttp:\n    enabled: true\n  httpclient:\n    enabled: false\n  client:\n    config:\n      default:\n        connectTimeout: 10000\n        readTimeout: 10000\n  compression:\n    request:\n      enabled: true\n      min-request-size: 8192\n    response:\n      enabled: true\n\n# 暴露监控端点\nmanagement:\n  endpoints:\n    web:\n      exposure:\n        include: \'*\'\n', '9928f41dfb10386ad38b3254af5692e0', '2020-05-20 12:00:00', '2024-08-29 12:14:45', 'nacos', '0:0:0:0:0:0:0:1', '', '', '通用配置', 'null', 'null', 'yaml', '', '');
INSERT INTO `config_info` VALUES (2, 'qingxinsaas-gateway-dev.yml', 'DEFAULT_GROUP', 'spring:\n  redis:\n    host: localhost\n    port: 6379\n    password: \n  cloud:\n    gateway:\n      discovery:\n        locator:\n          lowerCaseServiceId: true\n          enabled: true\n      routes:\n        # 认证中心\n        - id: qingxinsaas-auth\n          uri: lb://qingxinsaas-auth\n          predicates:\n            - Path=/auth/**\n          filters:\n            # 验证码处理\n            - CacheRequestFilter\n            - ValidateCodeFilter\n            - StripPrefix=1\n        # 代码生成\n        - id: qingxinsaas-gen\n          uri: lb://qingxinsaas-gen\n          predicates:\n            - Path=/code/**\n          filters:\n            - StripPrefix=1\n        # 定时任务\n        - id: qingxinsaas-job\n          uri: lb://qingxinsaas-job\n          predicates:\n            - Path=/schedule/**\n          filters:\n            - StripPrefix=1\n        # 系统模块\n        - id: qingxinsaas-system\n          uri: lb://qingxinsaas-system\n          predicates:\n            - Path=/system/**\n          filters:\n            - StripPrefix=1\n        # 文件服务\n        - id: qingxinsaas-file\n          uri: lb://qingxinsaas-file\n          predicates:\n            - Path=/file/**\n          filters:\n            - StripPrefix=1\n        # 工作流\n        - id: qingxinsaas-flowable\n          uri: lb://qingxinsaas-flowable\n          predicates:\n            - Path=/flowable/**\n          filters:\n            - StripPrefix=1\n\n# 安全配置\nsecurity:\n  # 验证码\n  captcha:\n    enabled: true\n    type: math\n  # 防止XSS攻击\n  xss:\n    enabled: true\n    excludeUrls:\n      - /system/notice\n      - /qingxinsaas-flowable/definition/save\n\n  # 不校验白名单\n  ignore:\n    whites:\n      - /auth/logout\n      - /auth/login\n      - /auth/register\n      - /system/tenant/domainName\n      - /*/v2/api-docs\n      - /*/v3/api-docs\n      - /csrf\n      - /doc.html\n\n# springdoc配置\nspringdoc:\n  webjars:\n    # 访问前缀\n    prefix:\n# knife4j\nknife4j:\n  gateway:\n    enabled: true\n    tags-sorter: order\n    operations-sorter: order\n    strategy: manual\n    routes:\n      - name: 系统模块\n        url: /qingxinsaas-system/v3/api-docs?group=default\n        service-name: qingxinsaas-system\n        context-path: /qingxinsaas-system\n        order: 1\n      - name: 代码生成\n        url: /qingxinsaas-gen/v3/api-docs?group=default\n        service-name: qingxinsaas-gen\n        context-path: /qingxinsaas-gen\n        order: 2\n      - name: 定时任务\n        url: /qingxinsaas-job/v3/api-docs?group=default\n        service-name: qingxinsaas-job\n        context-path: /qingxinsaas-job\n        order: 3\n      - name: 工作流\n        url: /qingxinsaas-flowable/v3/api-docs?group=default\n        service-name: qingxinsaas-flowable\n        context-path: /qingxinsaas-flowable\n        order: 4\n', '2d1e38cfbade56da4ee55728a6063e72', '2020-05-14 14:17:55', '2025-01-15 10:22:35', NULL, '0:0:0:0:0:0:0:1', '', '', '网关模块', 'null', 'null', 'yaml', '', '');
