rue\n    validate-on-migrate: true\n    # 当迁移发现数据库非空且存在没有元数据的表时，自动执行基准迁移，新建schema_version表\n    baseline-on-migrate: true\n\n# MyBatis Plus配置\nmybatis-plus:\n    # 搜索指定包别名\n    typeAliasesPackage: com.qingxinsaas.system\n    # 配置mapper的扫描，找到所有的mapper.xml映射文件\n    mapperLocations: classpath:mapper/**/*.xml\n\n#多租户配置\ntenant:\n  enable: true\n  column: tenant_id\n  ignoreTables:\n    - sys_tenant\n    - sys_datasource\n    - sys_tenant_package\n    - sys_dict_data\n    - sys_dict_type\n    - sys_job\n    - sys_job_log\n    - sys_notice\n# springdoc配置\nspringdoc:\n  gatewayUrl: http://localhost:8080/${spring.application.name}\n  api-docs:\n    # 是否开启接口文档\n    enabled: true\n  info:\n    # 标题\n    title: \'系统模块接口文档\'\n    # 描述\n    description: \'系统模块接口描述\'\n    # 作者信息\n    contact:\n      name: RuoYi\n      url: https://ruoyi.vip', 'd0942bc5069550bace38798756fb6a80', '2020-11-20 00:00:00', '2025-01-15 09:56:46', NULL, '0:0:0:0:0:0:0:1', '', '', '系统模块', 'null', 'null', 'yaml', '', '');
